import { Component, OnInit, Input } from '@angular/core';
import { Observable } from 'rxjs';
import { ContentfulService } from '../contentful.service';
import { Apollo, gql } from 'apollo-angular';
import data from '../../assets/data.json';
import { stringifyForDisplay } from '@apollo/client/utilities';

@Component({
  selector: 'app-items',
  templateUrl: './items.component.html',
  styleUrls: ['./items.component.css']
})
export class ItemsComponent implements OnInit {

  testData:[] = [];
  exportedData:[] = [];

  constructor(
    private api: ContentfulService,
    private apollo: Apollo) {
  }

  ngOnInit() {
    this.testData = JSON.parse(JSON.stringify(data.data.pageTemplateCollection.items));
    this.parseJSON(this.testData);
    // this.api.retrieveContent().subscribe((data) => {
    //   this.data.url = data.items[0].fields.url;
    //   this.data.seoTitle = data.includes.Entry[0].fields.title;
    //   this.data.description = data.includes.Entry[0].fields.description;
    //   this.data.isNoIndex = data.includes.Entry[0].fields.isNoIndex;
    //   console.log(this.data);
    // })

    // this.apollo.query({
    //   errorPolicy: "all",
    //   query: gql`
    //   {
    //     pageTemplateCollection {
    //       items{
    //         url
    //         seo{
    //           entryTitle
    //           description
    //           isNoIndex
    //         }
    //       }
    //     }
    //   }`
    // })
    // .subscribe((result: any) => {
    //   console.log(result);
    //   this.data2 = result.data.pageTemplateCollection.items;
    //   console.log(this.data2);
    // })
  }

  parseJSON(testData: any[]) {
    for (let item in testData) {
      var newObj = {
        url: "",
        title: "",
        description: "",
        isNoIndex: false,
        category: {},
      };

      var tmpArray = [];

      if (testData[item].seo === null) {
        continue;
      } else {

        testData[item].url = testData[item].url.replace("/home", "");
        tmpArray = testData[item].url.split("/");
        tmpArray.shift();

        Object.assign(newObj.category, tmpArray);

        testData[item].url = testData[item].url.replace("/", "https://www.rogers.com/");
        newObj.url = testData[item].url;
        newObj.title = testData[item].seo.entryTitle;
        newObj.description = testData[item].seo.description.substring(0, 80);
        newObj.isNoIndex = testData[item].seo.isNoIndex;

        testData[item] = newObj;
      }
    }
    console.log(testData);
  }
}
